// Generate duplicate merge report
const duplicates = $input.all();

if (duplicates.length === 0) {
  return { report: 'No duplicates found', count: 0 };
}

const report = `
**Duplicate Knowledge Entries Detected**

Found ${duplicates.length} potential duplicate pairs:

${duplicates.map((d, i) => `
${i + 1}. **Similarity: ${(d.json.similarity * 100).toFixed(1)}%**
   - Entry 1: ${d.json.title1} (ID: ${d.json.id1})
   - Entry 2: ${d.json.title2} (ID: ${d.json.id2})
   - Action: Review and merge if appropriate
`).join('\n')}

**Recommended Actions:**
1. Review each pair manually
2. Merge duplicates using Claude AI to consolidate information
3. Archive redundant entries
4. Update references in workflows

**Generated:** ${new Date().toISOString()}
`.trim();

return {
  report: report,
  count: duplicates.length,
  duplicates: duplicates.map(d => d.json)
};

// Generate low performance report
const lowPerformers = $input.all();

if (lowPerformers.length === 0) {
  return { report: 'No low-performing entries found', count: 0 };
}

const report = `
**Low-Performing Knowledge Entries**

Found ${lowPerformers.length} entries requiring review:

${lowPerformers.map((entry, i) => {
  const data = entry.json;
  return `
${i + 1}. **${data.title}**
   - Category: ${data.category}
   - Current Success Rate: ${((data.current_success_rate || 0) * 100).toFixed(1)}%
   - Calculated Success Rate: ${((data.calculated_success_rate || 0) * 100).toFixed(1)}%
   - Total Uses (90 days): ${data.total_uses || 0}
   - Avg Satisfaction: ${(data.avg_satisfaction || 0).toFixed(2)}/5
   - Status: ${data.calculated_success_rate < 0.7 ? '‚ö†Ô∏è Below 70% threshold' : 'üìä Significant change detected'}
`;
}).join('\n')}

**Recommended Actions:**
1. Review and update content based on recent feedback
2. Add clarifications or additional steps
3. Update NSW legislation references if outdated
4. Consider splitting complex entries
5. Add more examples or scenarios

**Generated:** ${new Date().toISOString()}
`.trim();

return {
  report: report,
  count: lowPerformers.length,
  entries: lowPerformers.map(e => e.json)
};

// Generate stale entries report
const staleEntries = $input.all();

if (staleEntries.length === 0) {
  return { report: 'No stale entries found', count: 0 };
}

const report = `
**Stale Knowledge Entries Requiring Review**

Found ${staleEntries.length} entries not updated in 6+ months:

${staleEntries.map((entry, i) => {
  const data = entry.json;
  return `
${i + 1}. **${data.title}**
   - Category: ${data.category}
   - Last Updated: ${new Date(data.updated_at).toLocaleDateString()}
   - Days Since Update: ${Math.floor(data.days_since_update)}
   - Action Required: Review for accuracy and relevance
`;
}).join('\n')}

**Recommended Actions:**
1. Verify NSW legislation references are current (check for 2025 reforms)
2. Update success rates and statistics
3. Add recent examples or case studies
4. Check for broken links to related entries
5. Archive if no longer relevant

**Generated:** ${new Date().toISOString()}
`.trim();

return {
  report: report,
  count: staleEntries.length,
  entries: staleEntries.map(e => e.json)
};

// Generate recurring issues report
const recurringIssues = $input.all();

if (recurringIssues.length === 0) {
  return { report: 'No recurring issues detected', count: 0 };
}

const report = `
**Recurring Issues Detected (Past 7 Days)**

Found ${recurringIssues.length} patterns of similar tickets:

${recurringIssues.map((issue, i) => {
  const data = issue.json;
  return `
${i + 1}. **Category: ${data.category}**
   - Cluster Size: ${data.cluster_size} similar tickets
   - Average Similarity: ${((data.avg_similarity || 0) * 100).toFixed(1)}%
   - Sample Tickets:
${(data.sample_tickets || []).slice(0, 3).map(t => `     - ${t.substring(0, 100)}...`).join('\n')}
   - ‚ö†Ô∏è **Action:** Consider creating or updating knowledge entry
`;
}).join('\n')}

**Proactive Response Recommendations:**
1. Create new knowledge base entries for recurring issues
2. Update existing entries with recent scenarios
3. Consider sending proactive communication to all owners
4. Schedule preventive maintenance if systemic issue
5. Update FAQs on website

**Generated:** ${new Date().toISOString()}
`.trim();

return {
  report: report,
  count: recurringIssues.length,
  issues: recurringIssues.map(i => i.json)
};

// Compile comprehensive maintenance report
const reports = $input.all();

const timestamp = new Date().toISOString();
const date = new Date().toLocaleDateString('en-AU', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

const duplicateReport = reports.find(r => r.json.duplicates) || { json: { report: 'No duplicates found', count: 0 } };
const performanceReport = reports.find(r => r.json.entries) || { json: { report: 'No low performers found', count: 0 } };
const staleReport = reports.find(r => r.json.entries && r.json.report.includes('Stale')) || { json: { report: 'No stale entries found', count: 0 } };
const recurringReport = reports.find(r => r.json.issues) || { json: { report: 'No recurring issues detected', count: 0 } };

const totalIssues = 
  (duplicateReport.json.count || 0) + 
  (performanceReport.json.count || 0) + 
  (staleReport.json.count || 0) + 
  (recurringReport.json.count || 0);

const comprehensiveReport = `
# NSW Strata Automation - Nightly Maintenance Report
**Date:** ${date}
**Generated:** ${timestamp}

---

## Executive Summary

**Total Issues Found:** ${totalIssues}
- Duplicate Entries: ${duplicateReport.json.count || 0}
- Low-Performing Entries: ${performanceReport.json.count || 0}
- Stale Entries: ${staleReport.json.count || 0}
- Recurring Issues: ${recurringReport.json.count || 0}

${totalIssues === 0 ? '‚úÖ **All systems operating normally. No issues detected.**' : '‚ö†Ô∏è **Action required on flagged items below.**'}

---

## 1. Duplicate Knowledge Entries

${duplicateReport.json.report}

---

## 2. Low-Performing Knowledge Entries

${performanceReport.json.report}

---

## 3. Stale Knowledge Entries

${staleReport.json.report}

---

## 4. Recurring Issues & Proactive Opportunities

${recurringReport.json.report}

---

## Database Statistics

_(Will be populated with actual queries)_

**Knowledge Base:**
- Total Active Entries: TBD
- Total Training Examples: TBD
- Average Success Rate: TBD%

**Recent Activity (Past 7 Days):**
- Tickets Processed: TBD
- Auto-Response Rate: TBD%
- Average Confidence Score: TBD

---

## Next Scheduled Maintenance

**Tomorrow:** ${new Date(Date.now() + 86400000).toLocaleDateString('en-AU', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })} at 2:00 AM AEST

---

*This is an automated report generated by the NSW Strata Automation Scheduled Maintenance workflow.*
`.trim();

return {
  report: comprehensiveReport,
  summary: {
    totalIssues: totalIssues,
    duplicates: duplicateReport.json.count || 0,
    lowPerformers: performanceReport.json.count || 0,
    staleEntries: staleReport.json.count || 0,
    recurringIssues: recurringReport.json.count || 0,
    timestamp: timestamp
  },
  individualReports: {
    duplicates: duplicateReport.json,
    performance: performanceReport.json,
    stale: staleReport.json,
    recurring: recurringReport.json
  }
};

